/* 归档 */
.article-sort {
    display: -webkit-box;
    display: -moz-box;
    display: -webkit-flex;
    display: -ms-flexbox;
    display: box;
    display: flex;
    -webkit-box-orient: horizontal;
    -moz-box-orient: horizontal;
    -o-box-orient: horizontal;
    -webkit-flex-direction: row;
    -ms-flex-direction: row;
    flex-direction: row;
    -webkit-box-lines: multiple;
    -moz-box-lines: multiple;
    -o-box-lines: multiple;
    -webkit-flex-wrap: wrap;
    -ms-flex-wrap: wrap;
    flex-wrap: wrap;
    -webkit-box-pack: justify;
    -moz-box-pack: justify;
    -o-box-pack: justify;
    -ms-flex-pack: justify;
    -webkit-justify-content: space-between;
    justify-content: space-between
}

.article-sort-title {
    padding-bottom: 0!important;
    position: relative;
    font-size: 2em;
    color: var(--text-highlight-color);
    font-weight: 700
}

.article-sort-item {
    position: relative;
    display: -webkit-box;
    display: -moz-box;
    display: -webkit-flex;
    display: -ms-flexbox;
    display: box;
    display: flex;
    -webkit-box-align: center;
    -moz-box-align: center;
    -o-box-align: center;
    -ms-flex-align: center;
    -webkit-align-items: center;
    align-items: center;
    margin: 7.5px 0;
    border-radius: 12px;
    padding: 8px;
    width: calc(50% - 7.5px);
    background: var(--ah-card-bg);
    -webkit-transition: all .2s ease-in-out;
    -moz-transition: all .2s ease-in-out;
    -o-transition: all .2s ease-in-out;
    -ms-transition: all .2s ease-in-out;
    transition: all .2s ease-in-out
}

@media screen and (max-width: 768px) {
    .article-sort-item {
        width:100%
    }

    .category-bar-item a {
        padding: .1rem .2rem
    }
}

.article-sort-item.no-article-cover {
    height: 80px;
    padding-left: 80px
}

.article-sort-item.year {
    font-size: 1.8em;
    font-weight: 700;
    margin-bottom: 0;
    background: 0 0;
    width: 100%;
    padding: 0
}

.article-sort-item.year:hover {
    background: 0 0
}

.article-sort-item-title {
    color: var(--font-color);
    font-size: 17px;
    -webkit-transition: all .3s;
    -moz-transition: all .3s;
    -o-transition: all .3s;
    -ms-transition: all .3s;
    transition: all .3s;
    -webkit-line-clamp: 2;
    font-weight: 700;
    line-height: 1.4em
}

.article-sort-item-title:hover {
    color: var(--btn-hover-color)
}

.article-sort-item-img {
    overflow: hidden;
    width: 160px;
    height: 90px;
    border-radius: 7px;
    border: 1px solid var(--ah-hover-bg)
}

@media screen and (max-width: 768px) {
    .article-sort-item-img {
        width: 100px;
        height: 80px
    }
}

.article-sort-item-img img {
    width: 100%;
    height: 100%;
    object-fit: cover;
    border-radius: 7px
}

.article-sort-item-info {
    -webkit-box-flex: 1;
    -moz-box-flex: 1;
    -o-box-flex: 1;
    box-flex: 1;
    -webkit-flex: 1;
    -ms-flex: 1;
    flex: 1;
    padding-left: 10px;
    padding: 0!important
}

.article-sort-item-info a.article-meta__tags {
    color: var(--dark-grey);
    font-size: 15px
}

.article-sort-item-info a.article-meta__tags:hover {
    color: var(--btn-hover-color)
}

.article-sort-item-info time.post-meta-date-created {
    bottom: 10px;
    right: 10px;
    position: absolute;
    font-size: 14px;
    line-height: 14px;
    color: var(--dark-grey)
}
